mapper.select:
select
from notification ug where 1=1 order by reg_date
limit ${limit} offset ${offset}

mapper.update:
update notification
set
  user_cnt = #{userCnt,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

update user_notification
set
  status = #{status,jdbcType=VARCHAR},
  send_id = #{sendId,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select nf.sender_name, us.user_name as receiver_name, un.status, un.reg_date , un.type
from user_notification  un
inner join users us on un.user_id = us.id
inner join notification nf on   un.notification_id = nf.id
where un.notification_id = #{filterId}

    limit ${limit} offset ${offset}
